import React from 'react';

function SourceCode({slide}) {
    // inside the slide.code there will be spans with the class names, insert the code in the div with the class name 'code fade'
    // check if the string matches the regex and return a boolean
    function checkRegex(str, regex) {
        const re = new RegExp(regex);
        return re.test(str);
        }

    const handleInputChange = (e) => {
        // get the value of the input
        const value = e.target.value;
        // get the regex attribute from the input
        const regex = e.regex;
        // check if the value matches the regex
        const isMatch = checkRegex(value, regex);
        console.log(value)
        // if it matches, add the class 'success' to the input, otherwise add the class 'error'
        e.target.className = isMatch ? 'success' : 'error';
        // set style to the input
        e.target.style = isMatch ? {borderColor: 'green'} : {borderColor: 'red'}
    };

    return (
        <div className="code fade">
            <pre>
                <code>
                    {slide.code}
                </code>
            </pre>
            <input type="text" regex={"/ni/"} onChange={handleInputChange}/>
        </div>
    )
}

export default SourceCode;
