function NewDemo() {
    var [data, setData] = useState({
        "variables":[],
        "slides":[]
    });

    var [currentSlide, setCurrentSlide] = useState(1)

    useEffect(() => {
        fetch("http://localhost:7777/api/6514782738287464273")
        .then((res) => res.json())
        .then((info) => {
            setData(info) ;
        })
            .catch((error) => console.log(error.message));  
        }, []);

    const handleSlideChange = (slideIndex) => {
        let r = slideIndex+currentSlide
        if(r<0){
        r = 0
        }else if(r>=data.slides.length){
        r = data.slides.length-1
        }
        setCurrentSlide(r)
    }

    return (
        <div className="App">
        <header className="App-header">
            <h1>MTFK HEADER</h1>
        </header>
        </div>
    );
    }
