import React, { useState } from 'react';
import './PopUpDemoID.css';
import { useNavigate } from 'react-router-dom';

const ModalComponent = ({idDemo, onClose}) => {
  const [copied, setCopied] = useState(false);
  const [message, setMessage] = useState('Your presentation is ready!');
  const [link, setLink] = useState('http://localhost:50740/demo/' + idDemo);
  let navigate = useNavigate();

  const handleGoToPresentation = () => {
    navigate("/demo/"+idDemo)
  };

  const handleClose = () => {
    onClose();
  }

  const handleCopyLink = () => {
    navigator.clipboard.writeText(link);
    setCopied(true);
    setTimeout(() => {
      setCopied(false);
    }, 2000);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={handleClose}>
          &times;
        </span>
        <p>{message}</p>
        <div className="button-container">
          <button onClick={handleGoToPresentation}>Go to Presentation</button>
          <button onClick={handleCopyLink}>
            {copied ? 'Copied!' : 'Share'}
          </button>
        </div>
        {copied && (
          <div className="link-container">
            <span>{link}</span>
          </div>
        )}
      </div>
    </div>
  );
};

export default ModalComponent;
