// a home page
import {React , useState, useRef} from 'react';
import TextBox from '../components/TextBox'; // Import the TextBox component
import Editor from "@uiw/react-codemirror";

const NewDemo = () => {
    var [text, setText] = useState(" ");
    const fileInputRef = useRef(null);

    const handleTextChange = (newText) => {
        setText(newText);
    };

    const handleCreateDemo = () => {
      console.log(text)
      // send a POST request to the server with the text
      fetch("http://localhost:7777/api/demo", {
          method: "POST",
          headers: {
              "Content-Type": "application/json"
          },
          body: JSON.stringify({'text': text})
      })
      .then((res) => res.json())
      .then((data) => {
          console.log(data);
      })
      .catch((error) => {
          console.error("Error creating demo:", error);
      });
    };

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            const reader = new FileReader();
            reader.onload = (e) => {
                setText(e.target.result);
            };
            reader.onerror = (e) => {
                console.error("Error reading file:", e);
            };
            reader.readAsText(selectedFile);
        }
      };

    const handleUploadFile = () => {
      fileInputRef.current.click();
    };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Create your own demonstration</h1>
      <div style={styles.content}>
        <div style={styles.buttonContainer}>
          <button onClick={handleCreateDemo} style={styles.button}>Create Demo</button>
          <button onClick={handleUploadFile} style={styles.button}>Upload File</button>
          <input type="file" ref={fileInputRef} style={{display:'none'}} onChange={handleFileChange} />
        </div>
        <Editor
      value={text}
      theme={colorMode}
      height={editorHeight}
      onChange={handleTextChange}
    />
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px'
  },
  header: {
    marginBottom: '20px'
  },
  content: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'flex-start',
    border: '2px solid black',
    borderRadius: '10px',
    padding: '10px',
    backgroundColor: '#f5f5dc' // Match the background color of the text box
  },
  buttonContainer: {
    display: 'flex',
    flexDirection: 'column',
    marginRight: '10px'
  },
  button: {
    marginBottom: '10px',
    padding: '10px',
    border: '2px solid black',
    borderRadius: '5px',
    backgroundColor: 'white',
    cursor: 'pointer'
  }
};

export default NewDemo;
