import './App.css';
import React, {useEffect, useState} from 'react';
import Annotations from './components/Annotation';
import SourceCode from './components/SourceCode';
import Navigation from './components/Navigation';

function App() {
  var [data, setData] = useState({
    "variables":[],
    "slides":[]
  });

  var [currentSlide, setCurrentSlide] = useState(0)
  var [currentVariables, setCurrentVariables] = useState([])

  useEffect(() => {
    fetch("http://localhost:7777/api/124124331124")
      .then((res) => res.json())
      .then((info) => {
        setData(info) ;
      })
        .catch((error) => console.log(error.message));  
      }, []);

  const handleSlideChange = (slideIndex) => {
    let r = slideIndex+currentSlide
    if(r<0){
      r = 0
    }else if(r>=data.slides.length){
      r = data.slides.length-1
    }
    setCurrentSlide(r)
    handleVariableChange()
    console.log(r)
  }

  const handleVariableChange = () => {
    // if there are commands in the current slide html code then add them to a list
    let coms = []
    let slide = data.slides[currentSlide]
    let html = slide.code
    let parser = new DOMParser()
    let doc = parser.parseFromString(html, 'text/html')
    // for every span element with the attribute command get the attribute value
    let spans = doc.getElementsByTagName("span")
    for(let i=0; i<spans.length; i++){
      let span = spans[i]
      let command = span.getAttribute("command")
      if(command){
        coms.push(command)
      }
    }
    setCurrentVariables(coms)
    return coms
  }

  return (
    <div className="App">
      <header className="App-header">
        {(data.slides.length + data.variables.length === 0) ? (
          <p>Loading...</p>
        ) : (
          <table className="page-table">
            <tr>
              <th style={{width:"75%"}} className="presentation-title"><h1>{data.slides[currentSlide].text}</h1></th>
              <th style={{width:"25%"}}></th>
            </tr>
            <tr>
              <td className="slideshow-container">
                <SourceCode slide = {data.slides[currentSlide]}/>
              </td>
              <td className="annotations-container">
               <Annotations variables = {data.variables}/> 
              </td>
            </tr>
            <tr>
              <td className="navigation-container">
                <Navigation slideChanger={handleSlideChange}/>
              </td>
              <td></td>
            </tr>
          </table>
        )}
      </header>
    </div>
  );
}

export default App;
