// a home page
import {React , useState} from 'react';
import TextBox from '../components/TextBox'; // Import the TextBox component

const NewDemo = () => {
    var [text, setText] = useState("");
    var [file, setFile] = useState(null);

    const handleTextChange = (newText) => {
        setText(newText);
      };

    const handleCreateDemo = () => {
      console.log("Current text:", text);
      // Here you can add any other action you want to perform with the text
    };

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
        console.log("File uploaded:", event.target.files[0].name)
    }

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Create your own demonstration</h1>
      <div style={styles.content}>
        <div style={styles.buttonContainer}>
          <button onClick={handleCreateDemo} style={styles.button}>Create Demo</button><input 
            type="file" ref={fileInputRef} style={styles.hiddenFileInput} onChange={handleFileChange} />

          <button style={styles.button}>Upload File</button>
        </div>
        <TextBox onTextChange={handleTextChange}/>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px'
  },
  header: {
    marginBottom: '20px'
  },
  content: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'flex-start',
    border: '2px solid black',
    borderRadius: '10px',
    padding: '10px',
    backgroundColor: '#f5f5dc' // Match the background color of the text box
  },
  buttonContainer: {
    display: 'flex',
    flexDirection: 'column',
    marginRight: '10px'
  },
  button: {
    marginBottom: '10px',
    padding: '10px',
    border: '2px solid black',
    borderRadius: '5px',
    backgroundColor: 'white',
    cursor: 'pointer'
  }
};

export default NewDemo;
