// a home page
import {React , useState, useRef} from 'react';
import TextBox from '../components/TextBox'; // Import the TextBox component

const NewDemo = () => {
    var [text, setText] = useState("");
    var [file, setFile] = useState(null);
    var fileInputRef = useRef(null);

    const handleTextChange = (newText) => {
        setText(newText);
    };

    const handleCreateDemo = () => {
        console.log("Current text:", text);
    };

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile) {
            const reader = new FileReader();
            reader.onload = (e) => {
                setText(e.target.result);
            };
            reader.readAsText(selectedFile);
            console.log(reader.result)
        }else{
            console.log("No file selected");
        }
    };

    const handleUploadFile = () => {
      fileInputRef.current.click();
    };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Create your own demonstration</h1>
      <div style={styles.content}>
        <div style={styles.buttonContainer}>
          <button onClick={handleCreateDemo} style={styles.button}>Create Demo</button>
          <button onClick={handleUploadFile} style={styles.button}>Upload File</button>
          <input type="file" ref={fileInputRef} style={{display:'none'}} onChange={handleFileChange} />
        </div>
        <TextBox onTextChange={handleTextChange}/>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px'
  },
  header: {
    marginBottom: '20px'
  },
  content: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'flex-start',
    border: '2px solid black',
    borderRadius: '10px',
    padding: '10px',
    backgroundColor: '#f5f5dc' // Match the background color of the text box
  },
  buttonContainer: {
    display: 'flex',
    flexDirection: 'column',
    marginRight: '10px'
  },
  button: {
    marginBottom: '10px',
    padding: '10px',
    border: '2px solid black',
    borderRadius: '5px',
    backgroundColor: 'white',
    cursor: 'pointer'
  }
};

export default NewDemo;
